#include "LIB::training.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {

    //gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    tit_createActionInfo(); 		// X-Framework용

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;

    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); 		// 필수 입력란을 설정한다.    
    ms_FormAuth = gfn_GetFormAuth(this); 	// 권한을 조회한다.

    if (ms_FormAuth == "W") {		 // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); 		// 저장 권한
    } 
    else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); 	// 읽기 권한
    } 
    else {
        gfn_Alert(MSGI00080);
        close();
    }
    // 공통 코드 조회
    gfn_SearchTrainingCourse("T", ds_oCourse, "Y", "선택"); // 직능 코드 조회
	//trace("shjung :: ds_oCourse ::" + ds_oCourse.SaveXML());

	// 소방안전관리자 && 생활안전강사 만
	for (var i = ds_oCourse.RowCount()-1; i >= 0; i--) {
		if (ds_oCourse.GetColumn(i, "CD") <> "20" && ds_oCourse.GetColumn(i, "CD") <> "10" &&  ds_oCourse.GetColumn(i, "CD") <> "21" 
		    && ds_oCourse.GetColumn(i, "CD") <> "22" && ds_oCourse.GetColumn(i, "CD") <> "23" && ds_oCourse.GetColumn(i, "CD") <> "24" 
		    && ds_oCourse.GetColumn(i, "CD") <> "27" && ds_oCourse.GetColumn(i, "CD") <> "28" && ds_oCourse.GetColumn(i, "CD") <> "29"  
		    && ds_oCourse.GetColumn(i, "CD") <> "50" && ds_oCourse.GetColumn(i, "CD") <> "53"
		    && ds_oCourse.GetColumn(i, "CD") <> "30" && ds_oCourse.GetColumn(i, "CD") <> "31") {			

			// shjung 220530 - '30','31'추가, 건설안전 소관, 업무대행감독
			// 생활안전 강사(50) 직능추가 20220926
			// 심폐소생술전문자격(53) 직능 추가 23.11 추가
			ds_oCourse.DeleteRow(i);
		}
	}    
    cbxCourse.Value = "22";
    gfn_SearchJibu(cbxJibu, "Y", ""); 			// 지부 코드 조회
    edTrainingYear.Text = left(GetDate(), 4);
	radSort.Value = 0;
    edTrainingOrder.SetFocus();
}


