    }
}


function lfn_Search(obj)
{
    if (!gfn_FormValidate(this)) { // 조회 Validation Check
        return false;
    }    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",회차=" + quote(edTrainingOrder.Text)
                + ",정렬방법=" + quote(radSort.Text);

    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );

    // 실무능력평가표
    ds_oEvaluation.ClearData(); // 데이타셋 초기화
    ds_oEvaluationPoto.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchTraining0145Evaluation", false);
	tit_addCsvSearchActionInfo("training:searchTraining0145EvaluationPoto", false);

	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oEvaluation=ds_oEvaluation ds_oEvaluationPoto=ds_oEvaluationPoto"	// outDs
		,"TOMGGTMGNO=" + quote(cbxJibu.Value)
            + " TOTCCOURSECD=" + quote(cbxCourse.Value)
            + " TOYEAR=" + quote(edTrainingYear.Text)
            + " TOTRAININGORDER=" + quote(edTrainingOrder.Text)
            + " SORT=" + quote(radSort.Value)// args 
		,"lfn_AfterSearchEvaluation"
		,false
	);	
}


/****************************************************************************************
* 기능 : 실무능력평가표 출력
****************************************************************************************/
function lfn_Print(obj)
{    
    if (ds_oEvaluationPoto.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }
    // 출력이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",회차=" + quote(edTrainingOrder.Text)
                + ",정렬방법=" + quote(radSort.Text);
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , "실무능력평가표출력"
        , ls_HistoryArg
    );    
    var PrtTime = "";

	// 12시 이전에는 발급일자만 출력
	// 12시 이후에는 발급일자+ 시간까지 출력	
	if (substr(GetDate(), 8,4) > '1200') {	
		PrtTime = left(GetDate(), 4)+"-"+substr(GetDate(), 4,2)+"-"+substr(GetDate(), 6,2)+" "+
					  substr(GetDate(), 8,2)+":"+substr(GetDate(), 10,2);

	} 
	else {	
		PrtTime = left(GetDate(), 4)+"-"+substr(GetDate(), 4,2)+"-"+substr(GetDate(), 6,2);		
	}	
	var sReportName = "";	
	if (cbxCourse.Value == "20") {	 
		sReportName = "frmtraining0145R00TrainingExecutiveAbilityEvaluation.rex";	
	} 
	else if (cbxCourse.Value == "22") {		
		sReportName = "frmtraining0145R01TrainingExecutiveAbilityEvaluation.rex";		
	} 
	else if (cbxCourse.Value == "10") {	
		sReportName = "frmtraining0145R02TrainingExecutiveAbilityEvaluation.rex";		
	} 
	else if (cbxCourse.Value == "21") {	
		sReportName = "frmtraining0145R03TrainingExecutiveAbilityEvaluation.rex";		
	} 
	else if (cbxCourse.Value == "23") {	
		sReportName = "frmtraining0145R04TrainingExecutiveAbilityEvaluation.rex";
	} 
	else if (cbxCourse.Value == "24") {	
		sReportName = "frmtraining0145R05TrainingExecutiveAbilityEvaluation.rex";		
	} 
	else if (cbxCourse.Value == "28") {	
		sReportName = "frmtraining0145R06TrainingExecutiveAbilityEvaluation.rex";		
	} 
	else if (cbxCourse.Value == "29") {	
		sReportName = "frmtraining0145R07TrainingExecutiveAbilityEvaluation.rex";		
	} 
	else if (cbxCourse.Value == "27") {	
		sReportName = "frmtraining0145R08TrainingExecutiveAbilityEvaluation.rex";		
	} 
	else if (cbxCourse.Value == "50") { //생활안전 강사 출력 추가 20220926

		sReportName = "frmtraining0030R05practicecapacityevaluation.rex";			
	} 
	else if (cbxCourse.Value == "53") { //심폐소생술전문자격 23.11 추가

		sReportName = "frmtraining0030R07ResuscitationEvaluation.rex";			
	} 
	else if (cbxCourse.Value == "30") {	// 22.09 추가 : 30:건설현장소방안전관리자
		sReportName = "frmtraining0145R09TrainingExecutiveAbilityEvaluation.rex";		
	} 
	else if (cbxCourse.Value == "31") {	// 22.09 추가 : 31:업무대행 감독자 
		sReportName = "frmtraining0145R10TrainingExecutiveAbilityEvaluation.rex";		
	}	
    gfn_CallReportPopup(sReportName
                      , "ds_oEvaluationPoto||ds_oEvaluation"//||ds_oTrainingAttendencePoto
                      , "rpDirpath=" + GF_FILE_SERVER_URL + "kems"                        
                        + "^PrtTime=" + PrtTime);
}


/* 공통 필수 종료 */ 
/****************************************************************************************
 * 기능 : 결강자 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
****************************************************************************************/
function lfn_AfterSearchEvaluation(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    lfn_Print(); // 실무능력평가표 출력 함수 호출
}


