Window.Script:
/*
 * 프로그램명 : frmtraining0147PTrainingEvaluationCancel.xml
 * 설명 : 합격취소팝업
 * 작성일 : 2016.03.08
 * 작성자 : 정현호
 */

#include "LIB::COMMON.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    // 공통 코드 조회
    gfn_SearchTrainingCourse("T", ds_oCourse, "Y", "선택"); // 직능 코드 조회
    cbxCourse.Value = "22";
    gfn_SearchJibu(cbxJibu, "Y", "선택"); // 지부 코드 조회
    edTrainingYear.Text = left(GetDate(), 4);
    
    edTrainingOrder.Text = sTrainingOrder;
    edTrainingOrder.SetFocus();
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 115 : // 저장(F4)
            lfn_Save();
            break;
    }
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
    if (!gfn_FormValidate(this)) { // 저장 Validation Check
        return false;
    }
    
    if (!gfn_Confirm("합격취소 처리를 하시겠습니까?")) {
        return false;
	}
    
    ds_oTrainingEvaluationCancel.ClearData(); // 데이타셋 초기화
    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
			+ ",년도=" + quote(edTrainingYear.Text)
			+ ",직능=" + quote(cbxCourse.Text)
			+ ",회차=" + quote(edTrainingOrder.Text)
			+ ",접수번호=" + quote(edSeq.Text);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , "실무능력평가 합격취소팝업"
        , ls_HistoryArg
    );

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchTraining0147EvaluationCancel", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTrainingEvaluationCancel=ds_oTrainingEvaluationCancel"	// outDs
		,"TOMGGTMGNO=" + quote(cbxJibu.Value)
			+ " TOYEAR=" + quote(edTrainingYear.Text)
			+ " TOTCCOURSECD=" + quote(cbxCourse.Value)
			+ " TOTRAININGORDER=" + quote(edTrainingOrder.Text)
			+ " TOHJUBSUNO=" + quote(edSeq.Text)	// args 
		,"lfn_AfterSearchTrainingEvaluationCancel"
		,false
	);     
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */

/* 공통 함수 */

/* 공통 이벤트 함수 */

/* 사용자 함수 */
/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
 var flag = 0;
 function lfn_AfterSearchTrainingEvaluationCancel(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    if (ds_oTrainingEvaluationCancel.GetCount() == 0 ||
    
		ds_oTrainingEvaluationCancel.GetColumn(0, "ERPASSYN") != "Y"){
    
        gfn_Alert("일치하는 합격자가 없습니다.");
        return false;    
    
    }


	lfn_SaveModifyHistory("U"); // 변경이력을 저장한다.
    
    tit_clearActionInfo();
	tit_addSingleActionInfo("training:updateTraining0147EvaluationCancel", "", 0, "");
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,""	// outDs
		,"TOMGJIBUCD=" + quote(cbxJibu.Value)
			+ " TOYEAR=" + quote(edTrainingYear.Text)
			+ " TOTCCOURSECD=" + quote(cbxCourse.Value)
			+ " TOTRAININGORDER=" + quote(edTrainingOrder.Text)
			+ " TOHJUBSUNO=" + quote(edSeq.Text)	// args 
		,"lfn_AfterSaveTrainingEvaluationCancel"
		,false
	);
}
 
function lfn_AfterSaveTrainingEvaluationCancel(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    edSeq.Text = ""; // 접수번호 초기화.
    parent.lfn_Search(); // 부모창 그리드 Redraw.
}

/* 회차 포커스 아웃될때 자동 0 입력 */
function edTrainingOrder_OnKillFocus(obj) {
	gfn_ComponentOnKillFocus(obj);

    obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3));    
}

function edSeq_OnKillFocus(obj) {
	gfn_ComponentOnKillFocus(obj);

    obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 6));
}

/*
 * 기능 : 업무 변경이력을 저장한다.
 * parameter : sGubun - 변경구분. 예) I, U, D
 * return : 없음
 */
function lfn_SaveModifyHistory(sGubun) {    
    gds_iModifyHistory.InsertRow(0);                 // 데이터셋 row 추가            
    gds_iModifyHistory.SetColumn(0, "MHWORKGUBUN", "합격취소"); // 업무구분
    gds_iModifyHistory.SetColumn(0, "MHGUBUN", sGubun); // 변경구분
    gds_iModifyHistory.SetColumn(0, "MHKEY", ds_oTrainingEvaluationCancel.GetColumn(0, "ERTJMGNO")); // KEY값
    gds_iModifyHistory.SetColumn(0, "MHCOLUMN", "실무능력평가결과"); // 변경 컬럼명
    gds_iModifyHistory.SetColumn(0, "MHBEFOREDATA", "N"); // 변경 전 값
    gds_iModifyHistory.SetColumn(0, "MHAFTERDATA", "Y"); // 변경 후 값        

    gfn_InsertModifyHistory(); // 업무 변경이력을 저장한다.
}
