#include "LIB::training.js"; // 공통업무 관련
var li_rowpos = 0;	// 데이터셋 row 위치
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회
    gfn_SearchTrainingCourse("L", ds_oCourse, "Y", "선택"); // 직능 코드 조회
    ds_oCourse.Sort("CD");		// 직능코드로 정렬

    cbxCourseStart.Value = "";
    cbxCourseEnd.Value = "";
    gfn_SearchJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회
    gfn_SearchCommonCode(ds_oPassGubun, "PASSGUBUN", "Y", "선택"); // 취득구분 코드 조회
    cbxPassGubunStart.Value = "";
    cbxPassGubunEnd.Value = "";

    cbxCourseStart.Value = "10";
    cbxCourseEnd.Value = "28";
    
    cbxPassGubunStart.Value = "3";
    cbxPassGubunEnd.Value = "7";
    
    calDateStart.Value = GetDate();
    calDateEnd.Value = GetDate();    
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdLicense.Height = nCy - gdLicense.Top - 4;
        gdLicense.Width = nCx - gdLicense.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_ioLicenseList.ClearData(); // 데이타셋 초기화

    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    ///////////////////// 조회이력 남기기
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
            + " 직능1=" + quote(cbxCourseStart.Value)  // 직능1
            + " 직능2=" + quote(cbxCourseEnd.Value)  // 직능2
            + " 취득구분1=" + quote(cbxPassGubunStart.Value)  // 취득구분1
            + " 취득구분2=" + quote(cbxPassGubunEnd.Value)   // 취득구분2
            + " 발급일자1=" + quote(calDateStart.Value)   // 발급일자1
            + " 발급일자2=" + quote(calDateEnd.Value)	    // 발급일자2;
            
            + " 발급구분=" + quote(radWorkGubun.Value);
    
      
        gfn_InsertSearchPrintHistory(
            "S"
            , this.ID
            , global.mnuLeft.cbxOpenForm.Text
            , ls_HistoryArg
        ); 
    /////////////////////      
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchLicensePrivacyList", false);
	// 서버 호출 	
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioLicenseList=ds_oLicenseList"	// outDs
		,"TOMGJIBU=" + quote(cbxJibu.Value)  //지부
            + " LTCCOURSECD=" + quote(cbxCourseStart.Value)  // 직능1
            + " LTCCOURSECD2=" + quote(cbxCourseEnd.Value)  // 직능2
            + " LISSUEGUBUN=" + quote(cbxPassGubunStart.Value)  // 취득구분1
            + " LISSUEGUBUN2=" + quote(cbxPassGubunEnd.Value)   // 취득구분2
            + " LHPRTDATE=" + quote(calDateStart.Value)     // 발급일자1
            + " LHPRTDATE2=" + quote(calDateEnd.Value)	    // 발급일자2  
            
            + " LHISSUEGUBUN=" + quote(radWorkGubun.Value)  // 발급구분
            
            + " REGISTERYN=" + quote(radRegisterYn.Value)	// args 
		,"lfn_AftersearchLicensePrivacyList"
		,false
	);
}

/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 결강자 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AftersearchLicensePrivacyList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	
    medExamSeat.Value = ds_ioLicenseList.count;  // 조회건수 표기함
    ds_ioLicenseList.rowposition = li_rowpos;	// 선택했던 커서로 이동
    
    li_rowpos = 0; // 커서 위치 memory 초기화
    gfn_SearchMessage(gdLicense); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
   
}

