function lfn_localExcel(obj)
{  
    if (ds_ioLicenseList.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return false;
    }
	lfn_Excel(obj);
}

// 더블클릭하여 개인정보동의서 등록하기
function gdLicense_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	li_rowpos = nRow;	// 선택한 row위치 memory

	var sArg = "sWorkGubun='01'"	// 강습관리
	
	    + " sBizGubun='013'" 		// 수첩발급

		+ " sCourse=" + quote(ds_ioLicenseList.GetColumn(nRow, "LTCCOURSECD"))
		
		+ " sPersonNM=" + quote(ds_ioLicenseList.GetColumn(nRow, "LHPERSONNM"))
		
		+ " sBIRTHDAY=" + quote(ds_ioLicenseList.GetColumn(nRow, "LHBIRTHDAY"))
	
		+ " sBizMgno1=" + quote(ds_ioLicenseList.GetColumn(nRow, "LLCSNO"));

	var arrPrivacy = Dialog("COM::frmCOM6100PPrivacyManage.xml", sArg, 624, 456, "", -1, -1);
	
	if (arrPrivacy) {
		lfn_Search();
	}
}

// 등록하기 버튼클릭하여 개인정보동의서 등록하기
function gdLicense_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{

	if (nCell == 8) {
		ds_ioLicenseList.row = nRow;	
		gdLicense_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex);
    }
}
