var gubun = "N";
/* 공통 필수 시작 */

function Form_OnLoadCompleted(obj) {

    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
		ls_FormId = split(ls_FormId[1], ".");

    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";
    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.
    //ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    ms_FormAuth = "W";

    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } 
    else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } 
    else {
        gfn_Alert(MSGI00080);
        close();
    }
    // 공통 코드 조회
    gfn_SearchTrainingCourse("T", ds_oCourse, "Y", "선택"); // 직능 코드 조회
	// 직능을 특급,1급,2급,생활안전강사만 남김 
	// shjung 220603, "30, 31" 삭제
	for(i = 0; i < ds_oCourse.RowCount(); i++) {
        if (ds_oCourse.GetColumn(i, "CD") == "30" ||  
            ds_oCourse.GetColumn(i, "CD") == "31" ||
			ds_oCourse.GetColumn(i, "CD") == "20" ||
            ds_oCourse.GetColumn(i, "CD") == "24" ||
            ds_oCourse.GetColumn(i, "CD") == "28" ||
            ds_oCourse.GetColumn(i, "CD") == "29" ) {

			ds_oCourse.DeleteRow(i);
			i = i - 1;		
		}
    }
    cbxCourse.Value = "22";
    gfn_SearchJibu(cbxJibu, "Y", "선택"); // 지부 코드 조회
    edExamOrder.SetFocus();
    radWorkGubun.value ="Y";
    edExamYear.Text = left(GetDate(), 4);
    calPassDate.Value = left(GetDate(), 8);
}


function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}


function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdExamJubsu.Height = nCy - gdExamJubsu.Top - 4;
        //gdExamJubsu.Width = nCx - gdExamJubsu.Left - 4;
    }
}



/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    } 
    ///////////////////// 조회이력 남기기
    ls_HistoryArg = "지부=" + quote(cbxJibu.value)   //-- 지부
              +" 년도=" + quote(edExamYear.text)  //-- 년도 
              +" 직능=" + quote(cbxCourse.value)   //-- 직능              
              +" 회차=" + quote(edExamOrder.text) // -- 회차             
              +" 시험전후구분=" + quote(radWorkGubun.value); // -- 접수번호// args 

    gfn_InsertSearchPrintHistory(
            "P"
            , this.ID
            , global.mnuLeft.cbxOpenForm.Text
            , ls_HistoryArg
    ); 
    /////////////////////          
    ds_ioJubsuList_BeforeAfter.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchJubsuList_BeforeAfter", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioJubsuList_BeforeAfter=ds_oJubsuList_BeforeAfter"	// outDs
		,"EOMGGTMGNO=" + quote(cbxJibu.Value)
            + " EOYEAR=" + quote(edExamYear.Text)
            + " EOTCCOURSECD=" + quote(cbxCourse.Value)	// args
            + " EOEXAMORDER=" + quote(edExamOrder.Text)	// args
            + " GU_BUN=" + quote(radWorkGubun.value)	// args

            // args
		,"lfn_AfterJubsuList"
		,false
	);
}



function lfn_AfterJubsuList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    medPassCnt.Value = "0";
    // medFailCnt.Value = ds_ioJubsuList_BeforeAfter.GetCount();    
    medExamDate.value = ds_ioJubsuList_BeforeAfter.GetColumn(0, "EOEXAMDATE"); 
    medFailCnt.text = ""; 
    gfn_SearchMessage(gdExamJubsu); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}



function lfn_Save(obj) {
    if(!gfn_CheckChangedGrid(this))  //선택한것이 있는지 체크 메세지 보임
    { alert("합격 처리대상자를 선택해 주십시오.");      
        return false;
    }        
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
		return false;
	}
    if(!Confirm("선택한 인원에 대해 수첩번호 생성을 하시겠습니까?")){  //선택한것이 있는지 체크 메세지 보임
        return false;
    }	
    ds_ioReport.copy(ds_ioJubsuList_BeforeAfter);  // 1) 원본 복사           
    ds_ioReport.Filter("SEL = '1'"); // 2) 복사 후 필터함         
    ds_ioReport2.copyf(ds_ioReport);  // 3) 필터한 값을 실제 사용 위해 다른 곳에 복사     
    ds_ioReport.ClearData(); // 4) 데이타셋 초기화  레포트 출력위해 필터된 2)번의 데이터셋을 초기화함

	var cnt = 0; // 결강자수	
	var licensecnt = 0; // 기수첩발급자수	
	var arrLicenseNM = array(ds_ioReport2.RowCount()); //기수첩발급자 성명	
	for(i = 0; i < ds_ioReport2.RowCount(); i++) {
        if (ds_ioReport2.GetColumn(i, "IMAGECHK") == 'N') {
            cnt += 1; // 사진없는인원 카운트
        }
        // 수첩이 발급되었는지 체크
        lfn_LicenseCheck(ds_ioReport2.GetColumn(i, "EJPERSONNM"),
                         ds_ioReport2.GetColumn(i, "EJPERSONKEY"),
                         ds_ioReport2.GetColumn(i, "EOTCCOURSECD"));                        

        if (ds_ioLicenseCheck.GetRowCount() > 0) {        
			licensecnt += 1; // 기수첩발급자수 카운트			
			arrLicenseNM[licensecnt-1] = ds_ioReport2.GetColumn(i, "EJPERSONNM");        
        }
    }

    if(licensecnt > 0) {
        gfn_Alert("기존에 수첩을 발급한 인원이 있습니다. 확인 바랍니다.\n\n" +        
				  " * 기존발급자 : " + arrLicenseNM);        
        return false;
    }    
    if(cnt > 0) {
        gfn_Alert("사진을 등록하지 않은 인원이 있습니다. 확인 바랍니다.");
        return false;
    }
	btnSave.Enable = false;	// 한번 클릭하면 비활성화 시킴

    var LLCSNO_A;
    LLCSNO_A = ds_ioJubsuList_BeforeAfter.GetColumn(0, "EOYEAR") + "-" +   //날짜
              mid(ds_ioJubsuList_BeforeAfter.GetColumn(0, "EOMGGTMGNO"),1,2) + "-" +   //지부
              ds_ioJubsuList_BeforeAfter.GetColumn(0, "EOTCCOURSECD")+ "-2-";   //직능
    // alert(LLCSNO_A);              
    tit_clearActionInfo();
    tit_AddMultiActionInfo(    
        "training:insertJubsuList_BeforeAfterPassLicense"	// insert  		
        , ""	// update  
        , ""	// delete
        , ""	// flag column 명
        , ""	// key sql id
        , 0	// key 증가 값
        , "" 
        , 0 
        , "N"	// 실행타입
    );
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iBeforeAfterPassLicense=ds_ioJubsuList_BeforeAfter:U"/// inDs       

        ,""	// outDs
        ,"TPSABUN=" + gds_oUserInfo.GetColumn(0, "PSABUN") // -- 사번 " 
                    +" LLCSNO_A=" + quote(LLCSNO_A)//  -- 수첩앞자리 번                    
                    +" PASSDATE=" + quote(calPassDate.Value)//  -- 합격일자	                    
        // args 
        ,"lfn_AfterSaveJubsuList"
        ,false
    );	
/*
    tit_clearActionInfo();
    tit_addSaveActionInfo(
        ""	    // insert
        , "training:updateExamJubsuPass"	// update
        , ""	                            // delete
        , ""	                            // flag column 명
        , ""	                            // key sql id
        , 0	                                // key 증가 값
        , "" 
        , 0 
        , "N"	                            // 실행타입
    );
    tit_addSaveActionInfo(
        ""	    // insert
        , "training:insertLicense"	// update
        , ""	                            // delete
        , ""	                            // flag column 명
        , ""	                            // key sql id
        , 0	                                // key 증가 값
        , "" 
        , 0 
        , "N"	                            // 실행타입
    );
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iJubsuList=ds_ioJubsuList:U ds_iLicense=ds_ioJubsuList:U"	// inDs
        ,""	// outDs
        ,""	// args 
        ,"lfn_AfterSaveJubsuList"
        ,false
    );
    */
}



function lfn_AfterSaveJubsuList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }     
    gfn_SaveMessage(); 
    lfn_Search();
    //gfn_Alert ("OMR 처리전 일괄 시험합격 처리가 완료되었습니다.");
}



/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnSave.Enable = bStatus; // 저장 버튼
    gdExamJubsu.Editable = bStatus;
}



/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdExamJubsu, 1); // 멀티 정렬을 실행한다.
}



function gdTraining_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	var lobj_Dataset = object(obj.BindDataset);
	if (nCell == 0) { // 선택
        if (obj.GetCellProp("head", nCell, "text") == 1) { // 전체가 이미 선택된 경우
            obj.SetCellProp("head", nCell, "text", 0);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SEL", "0");
            }
        } 
        else { // 전체가 이미 선택되지 않은 경우
            obj.SetCellProp("head", nCell, "text", 1);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SEL", "1");
            }
        }
	} 
	else { // 헤드 정렬
        lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex);
	}
}



function lfn_EnterNextFocus(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
        gfn_ComponentOnKillFocus(obj);
        obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3));  
        lfn_Search();
    }  
}



function btnToExcel_OnClick(obj)
{
    if ( ds_ioJubsuList.GetTotalRowCount() > 0 ) {
        lfn_Excel();
	} 
	else {
        gfn_Alert("조회를 먼저 하세요.");
	}
}



function edExamOrder_OnKillFocus(obj) {
    gfn_ComponentOnKillFocus(obj);
    obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3));        
}



function ds_ioJubsuList_BeforeAfter_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	var cnt = 0; // 결강자수
	// var cnt2 = 0; // 결강자수
	for(i = 0; i < ds_ioJubsuList_BeforeAfter.RowCount(); i++) {
        if (ds_ioJubsuList_BeforeAfter.GetColumn(i, "SEL") == 1) {
            cnt += 1; // 시험접수인원 카운트
        }
    }
    medFailCnt.text = cnt;   
}



function radWorkGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	ds_ioJubsuList_BeforeAfter.ClearData(); // 데이타셋 초기화
}



function cbxCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "10") {	// 특급이면 합격일자 보이기

		lbPassDate.Visible = true;
		calPassDate.Visible = true;
		calPassDate.Value = left(GetDate(), 8);	
	}
	else {	
		lbPassDate.Visible = false;
		calPassDate.Visible = false;	
		calPassDate.Value = "";	
	}
}



// 수첩발급되었는지 체크
function lfn_LicenseCheck(sPERSONNM, sPERSONKEY, sTCCOURSECD)
{
    ds_ioLicenseCheck.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchLicenseCheck", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioLicenseCheck=ds_oLicenseCheck"	// outDs
		,"PERSONNM=" + quote(sPERSONNM)
            + " PERSONKEY=" + quote(sPERSONKEY)
            + " COURSECD=" + quote(sTCCOURSECD)	// args
		,""
		,false		
		,false		
		,true
	);
}



function Form_OnActivate(obj) {
	gs_SysID = "[stm_01,bsn_4000,dsc_0015]";	// 개인정보접속기록 시스템ID 
}
