/* 공통 필수 시작 */

function Form_OnLoadCompleted(obj) {



    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리



    // 폼 환경설정

    ls_SystemGubun = gs_SystemGubun;

    ls_SystemMenuId = gs_SystemMenuId;

    var ls_FormId = split(sFormUrl, "::");

    ls_FormId = split(ls_FormId[1], ".");

    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";



    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.



    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.



    lfn_SetFormAuth(true); // 저장 권한



    // 로딩 시 시험명 리스트 출력

    lfn_SearchExamOrderList();        

	if (ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "10" ||
		ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "24") {

		gfn_MakeAnswers100(100, false); // 답안 입력란을 100개, 활성으로 생성한다.	

	} else if (ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "29") {

		gfn_MakeAnswers100(150, false); // 답안 입력란을 150개, 활성으로 생성한다.	

	} else {

		gfn_MakeAnswers(50, false); // 답안 입력란을 50개, 활성으로 생성한다.	

	}


    // 공통 코드 조회
    // 시험직능(E) / 수첩직능(L) / 강습직능(T)
    //gfn_SearchTrainingCourse("T", ds_oCourse, "Y", "없음"); // 직능 코드 조회



}



function Form_OnKeyDown(obj,objJubsuerObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

    // to do



    gfn_Form_OnKeyDown(obj,objJubsuerObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리

}



function Form_OnSize(obj,nCx,nCy,nState)

{

    if (gs_IsResizingForm == "Y") {

        gdExamAns.Height = nCy - gdExamAns.Top - 4;

        gdExamAns.Width = nCx - gdExamAns.Left - 4;

    }

}



/*

 *  시험명 리스트

 */

function lfn_SearchExamOrderList()

{    

    ldb.Sync = true;

    ds_oExamOrder.ClearData(); // 데이타셋 초기화

    ds_oExamOrder.Load("SvcLdb::searchExamOrderList.xml");

    ldb.Sync = false;

}



function lfn_AfterSearchExamOrderList(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }



    gfn_SearchMessage(gdExamAns); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.

}



/*

 * 기능 : 조회

 */

function lfn_Search(obj) {

    if (gfn_IsNull(edExamPlace.Text)) { // 입력 Validation Check

        gfn_Alert("시험장을 선택하십시오.");

        return false;

    }



    medJubsuCnt.Text = "";

    medNoJubsuCnt.Text = "";

    var repeat = "";

    if (ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "10"

		|| ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "21"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "22"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "23"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "50"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "51"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "53") {

        repeat = "1";

    } else {

        repeat = cbxExamRepeat.Value;

    }

    // 답안작성자 리스트

    ds_ioExamAns.ClearData(); // 데이타셋 초기화

    ldb.Sync = true;

    transaction("select",

                "SvcLdb::searchExamJubsu.xml",

                "",

                "ds_ioExamAns=output",

                "EOMGNO='" + ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "EOMGNO") + "'"

                + " EREXAMROOM='" + edExamPlace.Text + "'"

                + " EREXAMREPEAT='" + repeat + "'"                

                + " GUBUN='" + radSearchGubun.Value + "'",

                "lfn_AfterSearchExamAnsList");

    ldb.Sync = false;    

}



function lfn_AfterSearchExamAnsList(serviceid,errorcd,errormsg) {

    if ( errorcd < 0 ) {

        gfn_Alert(errormsg);

    }

    var repeat = "";

    if (ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "10"

		|| ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "21"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "22"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "23"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "50"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "51"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "53") {

        repeat = "1";

    } else {

        repeat = cbxExamRepeat.Value;

    }

    // 검색결과, 전체 답지    

    ldb.Sync = true;

    transaction("select",

                "SvcLdb::searchExamAnsCnt.xml",

                "",

                "ds_oExamAnsCnt=output",

                "EOMGNO='" + ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "EOMGNO") + "'"

                + " APREPEATNO='" + repeat + "'",

                "");

    ldb.Sync = false;

    medJubsuCnt.Text = ds_ioExamAns.GetCount();

    medNoJubsuCnt.Text = ds_oExamAnsCnt.GetColumn(0, "CNT");



    gfn_SearchMessage(gdExamAns); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.

}



// 답지 정보 세팅

function lfn_AfterSettingAnswerPaper(nErrorCode, strErrorMsg) {



    // 데이터셋 초기화

    for (var iQitemNumber = 0; iQitemNumber < 50; iQitemNumber++) {    

        object("ed" + ms_MAKE_QITEMES_EDIT_NAME + "Ans" + toString(iQitemNumber+1)).Value = "";

    }    

    // 데이터셋 세팅

    for (var iQitemNumber = 0; iQitemNumber < ds_ioAnswerPaper.GetRowCount(); iQitemNumber++) {   

        object("ed" + ms_MAKE_QITEMES_EDIT_NAME + "Ans" + toString(iQitemNumber+1)).Value = ds_ioAnswerPaper.getColumn(iQitemNumber, "APANSWER");        

    }



    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }   

}





/*

 * 기능 : 저장

 */

function lfn_Save(obj) {



    if (ds_ioExamAns.GetCount() < 1) { // 로딩할 데이타가 없는 경우

        gfn_Alert("저장할 답지가 없습니다.");

        return false;

    }



    if (gfn_IsNull(edSeq.Text)) {

        gfn_Alert("응시번호를 입력해주십시오.");

        edSeq.SetFocus();

        return;

    }



    if (gfn_IsNull(radCourse.Value)) {

        gfn_Alert("직능을 입력해주십시오.");

        radCourse.SetFocus();

        return;

    }



    if (gfn_IsNull(radTrainingOrderUseYn.Value)) {

        gfn_Alert("유형을 입력해주십시오.");

        radTrainingOrderUseYn.SetFocus();

        return;

    }



    if (ds_ioAnswerPaper.GetColumn(ds_ioAnswerPaper.currow, "APAPLEXAMNO") == edSeq.Text

        && ds_ioAnswerPaper.GetColumn(ds_ioAnswerPaper.currow, "APTCCOURSECD") == radCourse.Value

        && ds_ioAnswerPaper.GetColumn(ds_ioAnswerPaper.currow, "APALPHATYPE") == radTrainingOrderUseYn.Value) {

        gfn_Alert("변경항목이 없습니다.");

        return;       

    }



    if (!gfn_Confirm(MSGW00020)) { // 정말로 추가 저장 하시겠습니까?

        return false;

    }



    transaction("Save",

                "SvcLdb::updateExamAnswer.xml",

                "",

                "",

                "APAPLEXAMNO='"+ edSeq.Text + "'" + 

                " APTCCOURSECD='"+ radCourse.Value + "'" + 

                " APALPHATYPE='"+ radTrainingOrderUseYn.Value + "'" + 

                " APSEQ='"+ ds_ioAnswerPaper.GetColumn(ds_ioAnswerPaper.currow, "APSEQ") + "'", //args 

                "lfn_AfterSave");

}



function lfn_AfterSave(serviceid,errorcd,errormsg)

{

    if ( errorcd < 0 ) {

        gfn_Alert(errormsg);

    }

    gfn_SaveMessage(); // 저장 메세지를 처리한다.

    lfn_Search();

}



/*

 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용

 * parameter : strSvcID - 서비스ID

 *             nErrorCode - 에러코드

 *             strErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSaveExamAns(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }



    gfn_SaveMessage(); // 저장 메세지를 처리한다.

    lfn_Search(); // 조회

}



/*

 * 기능 : 버튼 활성/비활성시킨다.

 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)

 * return : 없음

 */

function lfn_SetFormAuth(bStatus) {

    btnSave.Enable = bStatus; // 저장

}



