var ls_ExamPlaceSeq = ""; // 시험장 일련번호

function btnSearchExamPlace_OnClick(obj)

{

    if (gfn_IsNull(ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "EOMGNO"))) {

        gfn_Alert("선택된 시험이 없습니다.");

        return false;

    }

    var ms_ExamMgno = ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "EOMGNO");

    var arrExamRoom = lfn_ExamRoomListPopup(ms_ExamMgno); // 시험장소 조회 팝업

    if (length(arrExamRoom) > 0) {

        ls_ExamPlaceSeq = arrExamRoom[0];

        edExamPlace.Text = arrExamRoom[1];        

    }

}



/*

 * 기능 : 시험장목록 팝업을 호출한다.

 * parameter : sExamOrder - 시험회차

 *             sExamNm - 시험명

 *             sExamRoomNm - 시험장명

 * return value : 시험장목록 정보를 가진 배열 오브젝트

 */

function lfn_ExamRoomListPopup(sExamOrder) {

    var sArg = "sExamOrder='" + sExamOrder + "'";

    return Dialog("training::frmtraining0531MExamRoomListPopupLocal.xml", sArg, 600, 400, "", -1, -1);

}



function btnNext_OnClick(obj)

{

    gs_SystemGubun = "training";

    gs_SystemMenuId = "0000000003";

    global.mnuLeft.tabMenu.tabpgMenu.Text = "강습관리";

    var ls_MenuId = "0000000083";

    var ls_MenuTitle = "채점";

    var ls_MenuUrl = "training::frmtraining0540MExamMarking.xml";



    global.mnuTop.tabTopMenu.TabIndex = gds_oTopMenu.FindRow("MSYSGUBUN", gs_SystemGubun);

    gfn_OpenChildWindow(ls_MenuUrl, ls_MenuTitle, ls_MenuId);

}



function ds_ioExamAns_OnRowPosChanged(obj,nOldRow,nRow)

{

    for (var iQitemNumber = 0; iQitemNumber < 50; iQitemNumber++) {

        object("ed" + ms_MAKE_QITEMES_EDIT_NAME + "Ans" + toString(iQitemNumber+1)).Value = "";

    }

    edExamClass.Text = "";

    edSeq.Text = "";

    radCourse.Value = "";

    radTrainingOrderUseYn.Value = "";



	ds_ioAnswerPaper.ClearData(); // 데이타셋 초기화

    ldb.Sync = true;

    transaction("select",

                "SvcLdb::searchAnswerPaperDetail.xml",

                "",

                "ds_ioAnswerPaper=output",

                "APSEQ='" + ds_ioExamAns.GetColumn(ds_ioExamAns.currow, "APSEQ")+ "'"

                + " APEOMGNO='" + ds_ioExamAns.GetColumn(ds_ioExamAns.currow, "APEOMGNO")+ "'"

                + " APREPEATNO='" + ds_ioExamAns.GetColumn(ds_ioExamAns.currow, "APREPEATNO")+ "'"

                + " APAPLEXAMNO='" + ds_ioExamAns.GetColumn(ds_ioExamAns.currow, "APAPLEXAMNO")+ "'"

                + " APNUMTYPE='" + ds_ioExamAns.GetColumn(ds_ioExamAns.currow, "APNUMTYPE")+ "'"

                + " APTCCOURSECD='" + ds_ioExamAns.GetColumn(ds_ioExamAns.currow, "EOTCCOURSECD")+ "'"

                + " APALPHATYPE='" + ds_ioExamAns.GetColumn(ds_ioExamAns.currow, "APALPHATYPE")+ "'",

                "");

    ldb.Sync = false;  



    var userAns = Split(Replace(Replace(ds_ioAnswerPaper.GetColumn(ds_ioAnswerPaper.currow, "APANSWER"), "//", "/ /"), "//", "/ /"), "/" );

    for (var iQitemNumber = 0; iQitemNumber < length(userAns); iQitemNumber++) {

        object("ed" + ms_MAKE_QITEMES_EDIT_NAME + "Ans" + toString(iQitemNumber+1)).Value = userAns[iQitemNumber];

    }

    edExamClass.Text = ds_ioAnswerPaper.GetColumn(ds_ioAnswerPaper.currow, "APEXAMROOM");

    edSeq.Text = ds_ioAnswerPaper.GetColumn(ds_ioAnswerPaper.currow, "APAPLEXAMNO");

    radCourse.Value = ds_ioAnswerPaper.GetColumn(ds_ioAnswerPaper.currow, "APTCCOURSECD");

    radTrainingOrderUseYn.Value = ds_ioAnswerPaper.GetColumn(ds_ioAnswerPaper.currow, "APALPHATYPE");



}



function ds_oExamOrder_OnRowPosChanged(obj,nOldRow,nRow)

{

    edExamPlace.Text = "";

	if (ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "10"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "21"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "22"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "23"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "50"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "51"

        || ds_oExamOrder.GetColumn(ds_oExamOrder.currow, "TCCOURSECD") == "53") {

        cbxExamRepeat.Value = "1";

        cbxExamRepeat.Visible = false;

        lbExamRepeat.Visible = false;

	} else {

        cbxExamRepeat.Visible = true;

        lbExamRepeat.Visible = true;

        cbxExamRepeat.Value = "1";        

	}

}
