#include "LIB::COMMON.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {

    gfn_Form_OnLoadCompleted(obj);

    if (IsExistVar("sTitle")) {
		if (sOverChk = "OK") {
			lsTitle.BkColor = "azure";
			lsMsg.LineSpacing = 10;
		} else {
			lsTitle.BkColor = "red";
			lsMsg.LineSpacing = 0;
		}
		lsTitle.Text = sTitle;
        lsMsg.Text = sMsg;
    }
}

function Form_OnUnloadCompleted(obj) {
    // to do

    gfn_Form_OnUnloadCompleted(obj);
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam);
}
/* 공통 필수 종료 */

function btnOK_OnClick(obj) {
    Close();
}
